mapper.select:
SELECT * FROM
travel
WHERE deleteYn = FALSE AND hiddenFl = FALSE LIMIT #{pageDto.start}, #{pageDto.pageLength}

SELECT boardNo, memNo, writerNm, subject, contents, likeCnt, hitCnt, complaintFl, foodCharge, transportCharge, etcCharge, budget, district, DATE_FORMAT(visitDt, '%Y-%m-%d') as visitDt, regDt, thumbnailImage FROM
travel
WHERE boardNo = #{boardNo}

mapper.update:
UPDATE


    travel

SET subject = #{subject}, contents = #{contents}
WHERE boardNo = #{boardNo}

UPDATE
travel
SET deleteYn = TRUE WHERE boardNo = #{boardNo}

UPDATE
travel
SET hitCnt = hitCnt + 1 WHERE boardNo = #{boardNo}

UPDATE
travel
SET likeCnt = likeCnt + 1 WHERE boardNo = #{boardDto.boardNo}

mapper.select:
SELECT memNo FROM


    travel

WHERE boardNo = #{boardNo}
