mapper.select:
SELECT status
FROM friendShip
WHERE toMemNo = #{toMemNo} AND fromMemNo = #{fromMemNo}

mapper.update:
UPDATE friendShip
SET status = #{status}
WHERE toMemNo = #{toMemNo} AND fromMemNo = #{fromMemNo}

mapper.select:
SELECT COUNT(*)
FROM friendShip
WHERE toMemNo = #{toMemNo} AND fromMemNo = #{fromMemNo}

SELECT friendList
FROM member
WHERE memNo = #{memNo}

mapper.delete:
UPDATE member
SET friendList = JSON_REMOVE(friendList, CONCAT('$.', #{fromMemNo}))
WHERE memNo = #{toMemNo}

UPDATE member
SET friendList = JSON_REMOVE(friendList, CONCAT('$.', #{toMemNo}))
WHERE memNo = #{fromMemNo}

mapper.update:
UPDATE member
SET friendList = JSON_INSERT(friendList, CONCAT('$.', #{toMemNo}), #{now})
WHERE memNo = #{fromMemNo}

UPDATE member
SET friendList = JSON_INSERT(friendList, CONCAT('$.', #{fromMemNo}), #{now})
WHERE memNo = #{toMemNo}
